package codechef;

import java.util.Scanner;

public class Coldplay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int t=sc.nextInt();
		for(int i=0;i<t;i++)
		{
		    int m=sc.nextInt();
		    int s=sc.nextInt();
		    int n=m/s;
		    System.out.println(n);
		}

	}

}
